let message = "";

// Okolní vlhkost
if (msg.payload.humidity > flow.get("surroundings_humidity_max")) {
    if (!flow.get("surroundings_humidity_alerted")) {
        message += `Surroundings humidity is too high! (${msg.payload.humidity}%)\n`;
    }

    flow.set("surroundings_humidity_alerted", true);
}
else if (msg.payload.humidity < flow.get("surroundings_humidity_min")) {
    if (!flow.get("surroundings_humidity_alerted")) {
        message += `Surroundings humidity is too low! (${msg.payload.humidity}%)\n`;
    }

    flow.set("surroundings_humidity_alerted", true);  
}
else {
    flow.set("surroundings_humidity_alerted", false);
}

// Okolní teplota
if (msg.payload.temperature > flow.get("surroundings_temperature_max")) {
    if (!flow.get("surroundings_temperature_alerted")) {
       message += `Surroundings temperature is too high! (${msg.payload.temperature}°C)\n`;
    }

    flow.set("surroundings_temperature_alerted", true);
}
else if (msg.payload.temperature < flow.get("surroundings_temperature_min")) {
